mapper.select:


    and `subject` like concat('%',#{subject},'%')
    and sender_id = #{senderId}
    and site_id = #{siteId}
    and site.site_name like concat('%',#{siteName},'%')

order by event.create_time desc

mapper.update:
update xf_work_event
subject = #{subject}, content = #{content}, sender_id = #{senderId}, create_time = #{createTime}, site_id = #{siteId},
where id = #{id}

mapper.select:
select l.id, l.subject, l.create_time createTime,s.site_name siteName,c.fullname senderName from xf_work_event l
left join xf_user_customer c on c.id=l.sender_id
left join xf_base_site s on s.id=l.site_id

    and l.sender_id = #{customerId}
    and l.site_id = #{siteId}
    and l.site_id in ( select id from xf_base_site where province= #{province}
        and city = #{city}
        and area = #{area}
        and street = #{street}
        )

    and l.subject like CONCAT(CONCAT('%', #{title}),'%')
    and c.fullname like CONCAT(CONCAT('%', #{reportMan}),'%')

order by l.create_time desc
limit #{page},#{limit}

select l.id, l.subject, l.content, l.create_time createTimeShow,s.site_name siteName,c.fullname senderName from xf_work_event l
left join xf_user_customer c on c.id=l.sender_id
left join xf_base_site s on s.id=l.site_id

    and l.sender_id = #{customerId}
    and l.site_id = #{siteId}
    and l.site_id in ( select id from xf_base_site where province= #{province}
        and city = #{city}
        and area = #{area}
        and street = #{street}
        )

    and l.subject like CONCAT(CONCAT('%', #{title}),'%')
    and c.fullname like CONCAT(CONCAT('%', #{reportMan}),'%')

order by l.create_time desc
